// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

contract Sanyangsam {

    string hashValue;
    mapping(string => address) owners;

    event AddHashValue(string hashString);
    event AddOwners(string plantation_id);

    function addHashValue(string memory _hash, string memory _plantation_id) public {
        require((owners[_plantation_id] == msg.sender));
        hashValue = _hash;

        emit AddHashValue(hashValue);
    }

    function addOwners(string memory _plantation_id) public {
        owners[_plantation_id] = msg.sender;
        emit AddOwners(_plantation_id);
    }

    function getOwners(string memory _plantation_id) public view returns (address) {
        return owners[_plantation_id];
    }
}
